import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet-async";
import SearchControls from "../../components/ProductsList/SearchControls";
import ProductTable from "../../components/ProductsList/ProductTable";
import Pagination from "../../components/ProductsList/Pagination";
import "./productsList.css";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [expandedRows, setExpandedRows] = useState({});
  const [fields] = useState(["Brand", "Category", "Product", "Location"]);
  const [selectedField, setSelectedField] = useState("Brand");
  const [tempSearchTerm, setTempSearchTerm] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortMode, setSortMode] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [loggedIn, setLoggedIn] = useState(false);
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      try {
        const API_BASE_URL =
          process.env.REACT_APP_API_BASE_URL || "http://localhost:5001/api";
        const response = await fetch(`${API_BASE_URL}/products`);
        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }
        const data = await response.json();
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error("API 응답이 배열이 아닙니다:", data);
          setProducts([]);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const filteredProducts = products.filter((product) => {
    if (!searchTerm) return true;
    const fieldValue = product[selectedField]
      ? String(product[selectedField])
      : "";
    return fieldValue.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const sortByMode = (a, b) => {
    if (!sortMode) return 0;
    const fieldA = a[selectedField] ? String(a[selectedField]) : "";
    const fieldB = b[selectedField] ? String(b[selectedField]) : "";
    // 예시: sortMode가 "brandAsc", "brandDesc" 등의 형태라고 가정
    if (sortMode.endsWith("Asc")) {
      return fieldA.localeCompare(fieldB);
    } else if (sortMode.endsWith("Desc")) {
      return fieldB.localeCompare(fieldA);
    }
    return 0;
  };
  const sortedProducts = sortMode
    ? [...filteredProducts].sort(sortByMode)
    : filteredProducts;

  // 페이지네이션 처리
  const totalPages = Math.ceil(sortedProducts.length / itemsPerPage);
  const currentProducts = sortedProducts.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // 행 확장/축소 토글
  const toggleExpand = (id) => {
    setExpandedRows((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (
      loginUsername === process.env.REACT_APP_LOGIN_USER &&
      loginPassword === process.env.REACT_APP_LOGIN_PASSWORD
    ) {
      setLoggedIn(true);
    } else {
      alert("로그인 정보가 잘못되었습니다.");
    }
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const handleDelete = async (id) => {
    if (!loggedIn) {
      alert("로그인이 필요합니다.");
      return;
    }
    if (!window.confirm("정말 삭제하시겠습니까?")) return;
    setLoading(true);
    try {
      const API_BASE_URL =
        process.env.REACT_APP_API_BASE_URL || "http://localhost:5001/api";
      const response = await fetch(`${API_BASE_URL}/products/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) {
        throw new Error("Failed to delete product");
      }
      // 삭제 후 제품 목록 갱신
      setProducts((prev) => prev.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    setSearchTerm(tempSearchTerm.trim());
    setCurrentPage(1);
  };

  const handleSearchReset = () => {
    setTempSearchTerm("");
    setSearchTerm("");
    setCurrentPage(1);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // 페이지네이션 핸들러
  const goToPrevPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const goToNextPage = () => {
    if (currentPage < totalPages) setCurrentPage((prev) => prev + 1);
  };

  return (
    <div className="productList-container">
      <Helmet>
        <title>Glowshelfie Products</title>
        <meta name="description" content="This is Glowshelfie Products List." />
      </Helmet>
      <div>
        <img src="/frontend/public/icons/main.svg" alt="mainImage" />
        <h1 className="title">Products List</h1>
      </div>
      <div className="login-section">
        {!loggedIn ? (
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={loginUsername}
              onChange={(e) => setLoginUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={loginPassword}
              onChange={(e) => setLoginPassword(e.target.value)}
            />
            <button type="submit">Login</button>
          </form>
        ) : (
          <button onClick={handleLogout}>Logout</button>
        )}
      </div>

      {/* 검색 및 정렬 컨트롤 */}
      <SearchControls
        fields={fields}
        selectedField={selectedField}
        setSelectedField={setSelectedField}
        tempSearchTerm={tempSearchTerm}
        setTempSearchTerm={setTempSearchTerm}
        handleKeyDown={handleKeyDown}
        handleSearch={handleSearch}
        handleSearchReset={handleSearchReset}
        sortMode={sortMode}
        setSortMode={setSortMode}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />

      {/* 제품 테이블 */}
      <ProductTable
        products={currentProducts}
        loading={loading}
        expandedRows={expandedRows}
        toggleExpand={toggleExpand}
        handleDelete={handleDelete}
        loggedIn={loggedIn}
      />

      {/* 페이지네이션 */}
      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        goToPrevPage={goToPrevPage}
        goToNextPage={goToNextPage}
      />
    </div>
  );
};

export default ProductsList;
